import React, { useState, useEffect } from "react";
import { Col, Row, Button, Form, Alert, Modal } from "react-bootstrap";
import { Formik } from "formik";

function Uploader() {
  const [error, setError] = useState("");
  const [file, setFile] = useState(null);

  const updateReportSettings = (values) => {
    if (file) {
      let data = new FormData();
      data.append("logo", file);

      //   fetch("/update_firm_logo", {
      //     method: "POST",
      //     body: data,
      //   })
      //     .then((response) => response.json())
      //     .then(() => {
      //       fetch(`/firm_info`)
      //         .then((res) => res.json())
      //         .then((info) => {
      //           setInitialValues(info);
      //         });
      //     });
    }

    const callback = (data) => {
      if (data["Error"]) {
        setError(data["Error"]);
      } else {
        setError("");
      }
    };
    // post(values, "/update_report_settings", callback);
  };

  return (
    <div
      style={{
        marginLeft: "20px",
        marginTop: "25vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <h1
        style={{
          marginBottom: "10px",
          color: "#6d02fa",
        }}
      >
        Report Settings
      </h1>
      <Formik
        onSubmit={(values) => {
          console.log("submitting");
        }}
      >
        {({ values, handleChange, handleSubmit }) => (
          <Form onSubmit={handleSubmit}>
            <Col md="6">
              <Form.Group controlId="formFile" className="mb-3">
                <Form.Label>Report Logo Image</Form.Label>
                <Form.Control
                  onChange={(e) => {
                    setFile(e.target.files[0]);
                  }}
                  type="file"
                  name="logo"
                />
              </Form.Group>
            </Col>

            <Button
              variant="dark"
              type="submit"
              style={{ marginTop: "25px", marginBottom: "10px" }}
            >
              Go
            </Button>
            {error !== "" && <Alert variant={"danger"}>{error}</Alert>}
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default Uploader;
